   const list = ['популярности', 'цене', 'алфавиту']

   const onCickListItem = (i) => {
      setSelected(i)
      setIsVisiblePopup(false)
   }

   return (
      <div className="sort">
         <div className="sort__label">
            <svg
               width="10"
               height="6"
               viewBox="0 0 10 6"
               fill="none"
               xmlns="http://www.w3.org/2000/svg"
